#' Plotting of the explained variances
#'
#'
#' @param subnet a list generated by the \code{\link{generate.pathways.network}} function
#' @import ggplot2
#' @import data.table
#' @export
#' @examples
#' data(qmdiab.data)
#' data(qmdiab.annos)
#' 
#' pathway.graph<-generate.pathways.network(data = qmdiab.data,
#'  annotations = qmdiab.annos, level = "Sub.pathway")
#'  gg.explained.variance(pathway.graph)
gg.explained.variance<-function(subnet, selection = NULL){
  groups <- names(subnet$representatives$expvar)
  
  if(!is.null(groups)){
  
    if (!is.null(selection)){
      groups <- groups[selection]
    }
    
    DT<-data.table(group=character(), principal.component.number=character(), explained.variance=numeric())
    for(group in groups){
      expvar<-subnet$representatives$expvar[[group]]
      DT<-rbind(DT, data.table(group=group, principal.component.number=1:length(expvar), explained.variance=expvar))
    }
    g<-ggplot(DT[principal.component.number==1], aes(x=factor(group), y=explained.variance))
    g<-g+geom_point()+ theme(axis.text.x = element_text(angle = 75, hjust = 1))+ylim(c(0,1))+xlab("")
    g+ylab("explained variance of eigenmetabolites")
  }else{
    message("No explained variances available if representatives are not eigenmetabolites.\n")
  }
}
